import { useState } from 'react';
import './App.css';

function App() {
  const [count, setCount] = useState(0);

  const addOne = (value) => {
    if (count < 10) {
      setCount(count + value)
    }
  };
  return (
    <div className="App">
      <h3>Counter: {count}</h3>
      <button onClick={() => addOne(1)}>Add 1</button>
      <button onClick={() => addOne(-1)}>Sub 1</button>
      {/* <button onClick={() => addOne()}>Sub 1</button> */}
    </div>
  );
}

export default App;
