import { useState } from 'react';
import './App.css';

function App() {
  const [count, setCount] = useState(0);

  const changeCount = (value) => {
    // if (count < 10) {
    setCount(count + value)
    // }
  };
  return (
    <div className="App">
      <h3>Counter: {count}</h3>
      {count < 10 && (
        <div>
          <button onClick={() => changeCount(1)}>Add 1</button>
          <button onClick={() => changeCount(-1)}>Sub 1</button>
        </div>
      )}
    </div>
  );
}

export default App;
