import { useState } from 'react';
import './App.css';

function App() {
  const [count, setCount] = useState(0);

  const addOne = () => {
    setCount(count+1)
  }
  return (
    <div className="App">
      <h3>Counter: {count}</h3>
      <button onClick={addOne}>Add 1</button>
    </div>
  );
}

export default App;
